# Player
player_x, player_y = 100, 100
player_angle = 0
player_speed = 2

# Set up screen
screen = pygame.display.set_mode((SCREEN_WIDTH, SCREEN_HEIGHT))
pygame.display.set_caption("Mini Doom")
clock = pygame.time.Clock()

def mapping(x, y):
    return (x // TILE_SIZE, y // TILE_SIZE)

def ray_casting(screen, px, py, pa):
    cur_angle = pa - FOV / 2
    for ray in range(NUM_RAYS):
        for depth in range(MAX_DEPTH):
            target_x = px + depth * math.cos(cur_angle)
            target_y = py + depth * math.sin(cur_angle)
            if mapping(target_x, target_y) in world_map:
                depth *= math.cos(pa - cur_angle)
                wall_height = min(PROJ_COEFF / (depth + 0.0001), SCREEN_HEIGHT)
                color = 255 / (1 + depth * depth * 0.0001)
                pygame.draw.rect(screen, (color, color, color),
                                 (ray * SCALE, HALF_HEIGHT - wall_height // 2, SCALE, wall_height))
                break
        cur_angle += DELTA_ANGLE

# Game loop
def run():
    global player_x, player_y, player_angle

    stamina = 100

    pygame.mouse.set_visible(False)
    pygame.event.set_grab(True)

    running = True
    while running:
        screen.fill((30, 30, 30))
        for event in pygame.event.get():
            if event.type == pygame.QUIT:
                running = False

        mouse_dx, _ = pygame.mouse.get_rel()
        player_angle += mouse_dx * 0.003

        keys = pygame.key.get_pressed()

        dx, dy = 0, 0
        if keys[pygame.K_w]:
            dx += player_speed * math.cos(player_angle)
            dy += player_speed * math.sin(player_angle)
        if keys[pygame.K_s]:
            dx -= player_speed * math.cos(player_angle)
            dy -= player_speed * math.sin(player_angle)
        if keys[pygame.K_a]:  # strafe left
            dx += player_speed * math.cos(player_angle - math.pi / 2)
            dy += player_speed * math.sin(player_angle - math.pi / 2)
        if keys[pygame.K_d]:  # strafe right
            dx += player_speed * math.cos(player_angle + math.pi / 2)
            dy += player_speed * math.sin(player_angle + math.pi / 2)
            
        MAX_STAMINA = 100

        if stamina > MAX_STAMINA:
            stamina = MAX_STAMINA
        if stamina < 0:
            stamina = 0
            
        if keys[pygame.K_LSHIFT] and stamina > 0:
            player_speed = 10 if player_speed < 1 else 2
            stamina -= 1
        else:
            player_speed = 2
            stamina += 0.5
            
        new_x = player_x + dx
        new_y = player_y

        if mapping(new_x, new_y) not in world_map:
            player_x = new_x

        new_x = player_x
        new_y = player_y + dy

        if mapping(new_x, new_y) not in world_map:
            player_y = new_y

        if keys[pygame.K_ESCAPE]:
            running = False

        ray_casting(screen, player_x, player_y, player_angle)
        pygame.display.flip()
        clock.tick(FPS)

        print(stamina)

    pygame.quit()

if __name__ == '__main__':
    run()
